pragma solidity ^0.4.7;

contract Authentication  {

  struct User{
    string name;
    bool isfarmer;
    int creditScore; // can accept -ve values
    string[] auctionIds;
    bool exists;
  }

  User userObj;
  mapping (address => User) public users;

  uint private id; // Stores user id temporarily

  modifier onlyExistingUser {
    // Check if user exists or terminate

    require(users[msg.sender].exists);
    _;
  }

  // modifier onlyValidName(string name) {
  //   // Only valid names allowed

  //   require(!(name == 0x0));
  //   _;
  // }

  modifier shouldHaveValue(){
      require(msg.value > 0);
      _;
  }

  modifier onlyFarmer(){
      require(users[msg.sender].isfarmer, "Caller is not a farmer!");
      _;
  }

  constructor() public{      
  }

  function login() constant
  public
  onlyExistingUser
  returns (string) {
    return (users[msg.sender].name);
  }

  function signup(string name, bool _isFarmer, int _creditScore)
  public{
    // Check if user exists.
    // If yes, return user name.
    // If no, check if name was sent.
    // If yes, create and return user.

    userObj = User({
            name : name,
            isfarmer : _isFarmer,
            creditScore : _creditScore,
            auctionIds : new string [] (0),
            exists : true
        });

    users[msg.sender] = userObj;
  }

  // function update(string name)
  // public
  // onlyValidName(name)
  // onlyExistingUser
  // returns (string) {
  //   // Update user name.

  //   if (users[msg.sender].name != 0x0)
  //   {
  //       users[msg.sender].name = name;

  //       return (users[msg.sender].name);
  //   }
  // }

  function getUser(address _userAddr) public view returns(bool exists, bool isFarmer, string name){
    exists = users[_userAddr].exists;
    isFarmer = users[_userAddr].isfarmer;
    name = users[_userAddr].name;
  }
}
